<?phpclass ModelCatalogTestimonial extends Model {	public function addTestimonial($data) {		$this->db->query("INSERT INTO " . DB_PREFIX . "testimonial SET status = '" . (int)$this->request->post['status'] . "', sort_order = '" . (int)$this->request->post['sort_order'] . "',date_added = date('Y-m-d')");		$testimonial_id = $this->db->getLastId(); 					foreach ($data['testimonial_description'] as $language_id => $value) {			$this->db->query("INSERT INTO " . DB_PREFIX . "testimonial_description SET testimonial_id = '" . (int)$testimonial_id . "', language_id = '" . (int)$language_id . "', title = '" . $this->db->escape($value['title']) . "', description = '" . $this->db->escape($value['description']) . "'");		}		$this->cache->delete('testimonial');	}		public function editTestimonial($testimonial_id, $data) {		$this->db->query("UPDATE " . DB_PREFIX . "testimonial SET status = '" . (int)$this->request->post['status'] . "', sort_order = '" . (int)$data['sort_order'] . "' WHERE testimonial_id = '" . (int)$testimonial_id . "'");		$this->db->query("DELETE FROM " . DB_PREFIX . "testimonial_description WHERE testimonial_id = '" . (int)$testimonial_id . "'");							foreach ($data['testimonial_description'] as $language_id => $value) {			$this->db->query("INSERT INTO " . DB_PREFIX . "testimonial_description SET testimonial_id = '" . (int)$testimonial_id . "', language_id = '" . (int)$language_id . "', title = '" . $this->db->escape($value['title']) . "', description = '" . $this->db->escape($value['description']) . "'");		}				$this->cache->delete('testimonial');	}		public function deleteTestimonial($testimonial_id) {		$this->db->query("DELETE FROM " . DB_PREFIX . "testimonial WHERE testimonial_id = '" . (int)$testimonial_id . "'");		$this->db->query("DELETE FROM " . DB_PREFIX . "testimonial_description WHERE testimonial_id = '" . (int)$testimonial_id . "'");		$this->cache->delete('testimonial');	}		public function getTestimonial($testimonial_id) {		$query = $this->db->query("SELECT DISTINCT * FROM " . DB_PREFIX . "testimonial WHERE testimonial_id = '" . (int)$testimonial_id . "'");				return $query->row;	}			public function getTestimonials($data = array()) {		if ($data) {			$sql = "SELECT * FROM " . DB_PREFIX . "testimonial t LEFT JOIN " . DB_PREFIX . "testimonial_description td ON (t.testimonial_id = td.testimonial_id) WHERE td.language_id = '" . (int)$this->config->get('config_language_id') . "'";            $sql .= " ORDER BY td.title";            $sql .= " ASC";			$query = $this->db->query($sql);			return $query->rows;		} else {			$testimonial_data = $this->cache->get('testimonial.' . $this->config->get('config_language_id'));					if (!$testimonial_data) {				$query = $this->db->query("SELECT * FROM " . DB_PREFIX . "testimonial t LEFT JOIN " . DB_PREFIX . "testimonial_description td ON (t.testimonial_id = td.testimonial_id) WHERE td.language_id = '" . (int)$this->config->get('config_language_id') . "' ORDER BY td.title");					$testimonial_data = $query->rows;							$this->cache->set('testimonial.' . $this->config->get('config_language_id'), $testimonial_data);			}					return $testimonial_data;					}	}		public function getTestimonialDescriptions($testimonial_id) {		$testimonial_description_data = array();				$query = $this->db->query("SELECT * FROM " . DB_PREFIX . "testimonial_description WHERE testimonial_id = '" . (int)$testimonial_id . "'");		foreach ($query->rows as $result) {			$testimonial_description_data[$result['language_id']] = array(				'title'       => $result['title'],				'description' => $result['description']			);		}				return $testimonial_description_data;	}		public function getTotalTestimonials() {      	$query = $this->db->query("SELECT COUNT(*) AS total FROM " . DB_PREFIX . "testimonial");		return $query->row['total'];	}	}?>